import React, { useState } from "react";
import InputFormComponent from "./components/InputForm";
import DisplayCard from "./components/DisplayCard";

export default function App() {
  const [formData, setFormData] = useState({ name: "", location: "" });
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    if (!formData.name || !formData.location) return alert("All fields required");
    setLoading(true);
    const res = await fetch("http://localhost:5000/business-data", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    });
    const result = await res.json();
    setData(result);
    setLoading(false);
  };

  const regenerateHeadline = async () => {
    const res = await fetch(`http://localhost:5000/regenerate-headline?name=${formData.name}&location=${formData.location}`);
    const result = await res.json();
    setData((prev) => ({ ...prev, headline: result.headline }));
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-4">
      <h1 className="text-2xl font-bold mb-4">Business SEO Dashboard</h1>
      <InputFormComponent formData={formData} setFormData={setFormData} handleSubmit={handleSubmit} />
      {loading ? (
        <p className="mt-6 text-blue-500">Loading...</p>
      ) : (
        data && <DisplayCard data={data} regenerateHeadline={regenerateHeadline} />
      )}
    </div>
  );
}
